    // Bạn cũng cần class context này để kết nối Tray Icon và các Form
    public class SnowApplicationContext : ApplicationContext
    {
        private SnowBackgroundForm mainForm;
        private SettingsForm settingsForm;
        private NotifyIcon trayIcon;

        // Chuỗi Base64 chuẩn cho 1 icon bông tuyết nhỏ (đã loại bỏ tiền tố gây lỗi)


        // Chuỗi Base64 chuẩn cho 1 icon bông tuyết nhỏ (đã loại bỏ tiền tố gây lỗi)
        private string iconBase64 = @"";

        private Icon GetIconFromBase64(string base64)
        {
            try
            {
                byte[] bytes = Convert.FromBase64String(base64);
                using (var ms = new System.IO.MemoryStream(bytes))
                {
                    // Nếu là file PNG/JPG, cần chuyển qua Bitmap trước rồi mới sang Icon
                    using (Bitmap bmp = new Bitmap(ms))
                    {
                        return Icon.FromHandle(bmp.GetHicon());
                    }
                }
            }
            catch
            {
                return SystemIcons.Application; // Trả về icon mặc định nếu lỗi
            }
        }

        public SnowApplicationContext()
        {
            mainForm = new SnowBackgroundForm();
            settingsForm = new SettingsForm();

            // app icon

            settingsForm.Icon = GetIconFromBase64(iconBase64);
            //

            mainForm.Show();

            trayIcon = new NotifyIcon();
            // Bạn nên có một file icon tên appicon.ico trong thư mục debug, 
            // nếu không nó sẽ lấy icon mặc định của Windows
            trayIcon.Icon = GetIconFromBase64(iconBase64);
            trayIcon.Text = "Snow Fall Desktop";
            trayIcon.Visible = true;


            trayIcon.DoubleClick += delegate
            {
                if (settingsForm == null || settingsForm.IsDisposed) settingsForm = new SettingsForm();

                if (settingsForm.Visible)
                {
                    settingsForm.Activate();
                }
                else
                {
                    settingsForm.Show();
                    settingsForm.Activate();
                }
            };

            ContextMenu menu = new ContextMenu();

            menu.MenuItems.Add("Cài đặt / Settings", delegate
            {
                if (settingsForm == null || settingsForm.IsDisposed) settingsForm = new SettingsForm();

                if (settingsForm.Visible)
                {
                    settingsForm.Activate();
                }
                else
                {
                    settingsForm.Show();
                    settingsForm.Activate();
                }
            });

            menu.MenuItems.Add("Dọn sạch tuyết/ Clean", delegate { mainForm.ClearStuckSnow(); });

            // 2. THÊM NÚT GIỚI THIỆU Ở ĐÂY

            menu.MenuItems.Add("Giới thiệu / About", delegate
            {
                ShowAboutDialog();
            });

            menu.MenuItems.Add("-"); // Dấu gạch ngang phân cách
            // 3. Exit
            menu.MenuItems.Add("Thoát/Exit", delegate
            {
                trayIcon.Visible = false;
                Application.Exit();
            });
            trayIcon.ContextMenu = menu;
        }

