    // 1. PHẢI ĐẶT SnowConfig Ở ĐÂY ĐỂ TẤT CẢ CÁC CLASS TRONG NAMESPACE ĐỀU THẤY
    public static class SnowConfig
    {
        public static int SoLuongTuyet = 50;// SnowConfig.SoLuongTuyet
        public static int SoLuongTuyetToiDaTrenManHinh = 1000;// SnowConfig.SoLuongTuyetToiDaTrenManHinh
        public static int TocDoMin = 2;// SnowConfig.TocDoMin
        public static int TocDoMax = 5;// SnowConfig.TocDoMax
        public static int ThoiGianTanS = 20;// SnowConfig.ThoiGianTanS
        public static int BanKinhQuet = 80;// SnowConfig.BanKinhQuet
        public static Color MauTuyet = Color.White;// SnowConfig.MauTuyet
        public static int CuongDoGio = 15;// SnowConfig.CuongDoGio
        public static int ThoiGianThoiS = 5;// SnowConfig.ThoiGianThoiS
        public static int ThoiGianNgatS = 3;// SnowConfig.ThoiGianNgatS
        public static int GioiHamMatDo = 5;// SnowConfig.GioiHamMatDo
        public static int KichThuoc = 20; // SnowConfig.KichThuoc
        public static int DoDongBang = 0;// SnowConfig.DoDongBang
        public static int TyLeChieuRongManHinh = 50;// SnowConfig.TyLeChieuRongManHinh
        public static int TyLeChieuCaoManHinh = 50;// SnowConfig.TyLeChieuCaoManHinh
        public static int MucFPS = 24; // SnowConfig.MucFPS


        public static float TyLeNoiMacDinh = 0.16f;    // SnowConfig.TyLeNoiMacDinh 
        public static float TyLeNoiSutLo = 0.25f;   // SnowConfig.TyLeNoiSutLo 
        public static float TyLeNoiVatCung = 0.81f; // SnowConfig.TyLeNoiVatCung

        // Bộ gốc bất biến
        public const string KyTuMacDinh = "❄,❅,❆,✻,.,°"; // SnowConfig.KyTuMacDin
        public static string KyTuThem = ""; // SnowConfig.KyTuThem


        public const string ChuoiBiMat = "❤️[★] Xin chao!❤️"; // Nội dung bí mật
        public static int TiLeBiMat = 50000; // 1/1000 cơ hội xuất hiện


        public static string[] GetActiveShapes()
        {
            string chuoiGop = string.IsNullOrEmpty(KyTuThem) ? KyTuMacDinh : KyTuMacDinh + "," + KyTuThem;
            return chuoiGop.Split(new char[] { ',' }, StringSplitOptions.RemoveEmptyEntries);
        }

    }


    // 2. Class Snowflake (Dùng SnowConfig để Reset)
    public class Snowflake
    {
        public int X, Y, Speed, Size, LifeTime, Opacity;
        public string Shape;
        public bool IsStuck = false;
        public bool IsFallingAgain = false; // <-- THÊM DÒNG NÀY
        public float Angle;
        public float RotationSpeed;

        //private static string[] shapes = SnowConfig.GetActiveShapes();

        public Snowflake(int x, int y, Random r)
        {
            this.X = x;
            this.Y = y;
            Reset(Screen.PrimaryScreen.Bounds.Width, r);
        }

        public void Reset(int w, Random r)
        {
            // 1. Cài đặt vị trí và trạng thái cơ bản
            this.X = r.Next(w);
            this.Y = -30;
            this.IsStuck = false;
            this.IsFallingAgain = false;
            this.Opacity = 255;

            // 2. Tính toán tốc độ rơi ngẫu nhiên
            int minSpeed = SnowConfig.TocDoMin;
            int maxSpeed = SnowConfig.TocDoMax;
            this.Speed = (minSpeed >= maxSpeed) ? minSpeed : r.Next(minSpeed, maxSpeed + 1);

            // 3. Tính toán kích thước (biến thiên 80% - 120%)
            int baseSize = SnowConfig.KichThuoc;
            this.Size = r.Next((int)(baseSize * 0.8), (int)(baseSize * 1.2));

            // 4. QUYẾT ĐỊNH HÌNH DÁNG (SHAPE)
            // Kiểm tra tỷ lệ rơi trúng chuỗi bí mật (Easter Egg)
            // Ví dụ: SnowConfig.TiLeBiMat = 1000 thì xác suất là 1/1000
            if (r.Next(0, SnowConfig.TiLeBiMat) == 1)
            {
                this.Shape = SnowConfig.ChuoiBiMat; // Chuỗi đặc biệt (ví dụ: "LUCKY")
            }
            else
            {
                // Nếu không trúng bí mật, lấy ngẫu nhiên từ danh sách người dùng nhập + mặc định
                string[] currentShapes = SnowConfig.GetActiveShapes();
                if (currentShapes.Length > 0)
                {
                    this.Shape = currentShapes[r.Next(currentShapes.Length)].Trim();
                }
                else
                {
                    this.Shape = "❄"; // Phòng hờ danh sách rỗng
                }
            }

            // 5. Cài đặt góc xoay
            this.Angle = r.Next(0, 360);
            this.RotationSpeed = (float)(r.NextDouble() * 2 - 1);
        }









    }

    // 3. Form hiển thị chính
    public class SnowBackgroundForm : Form
    {
        private Timer timer = new Timer();
        private List<Snowflake> fallingFlakes = new List<Snowflake>();
        private List<Snowflake> stuckFlakes = new List<Snowflake>();
        private Random rand = new Random();
        private int taskbarTop;
        private float currentWindX = 0, targetWindX = 0;
        private int cycleTimerCount = 0;
        private bool isWindyPeriod = true;

        private Dictionary<string, Bitmap> snowCache = new Dictionary<string, Bitmap>();
        private Color lastMauTuyet = Color.Empty;

        [DllImport("user32.dll")] static extern short GetAsyncKeyState(int vKey);
        [DllImport("user32.dll")] static extern int GetWindowLong(IntPtr hWnd, int nIndex);
        [DllImport("user32.dll")] static extern int SetWindowLong(IntPtr hWnd, int nIndex, int dwNewLong);
        [DllImport("user32.dll")] static extern bool GetWindowRect(IntPtr hWnd, out RECT lpRect);
        [DllImport("user32.dll")] static extern IntPtr FindWindow(string lpClassName, string lpWindowName);
        [DllImport("user32.dll")] static extern IntPtr WindowFromPoint(Point point);
        [DllImport("user32.dll")] static extern bool SetWindowPos(IntPtr hWnd, IntPtr hWndInsertAfter, int X, int Y, int cx, int cy, uint uFlags);
        [DllImport("user32.dll")] static extern IntPtr GetAncestor(IntPtr hWnd, uint gaFlags);
        const uint GA_ROOT = 2; // Lấy cửa sổ cha cao nhất

        static readonly IntPtr HWND_BOTTOM = new IntPtr(1);
        public struct RECT { public int Left, Top, Right, Bottom; }



        public SnowBackgroundForm()
        {
            // 1. Thiết lập Styles để kiểm soát hoàn toàn việc vẽ, chặn nhấp nháy
            this.SetStyle(ControlStyles.AllPaintingInWmPaint |  // Không cho Windows vẽ nền riêng
                ControlStyles.UserPaint |             // Form tự vẽ hoàn toàn
                ControlStyles.OptimizedDoubleBuffer | // Dùng bộ đệm phụ
                ControlStyles.ResizeRedraw, true);
            this.UpdateStyles();

            this.FormBorderStyle = FormBorderStyle.None;
            this.Bounds = Screen.PrimaryScreen.Bounds;
            this.ShowInTaskbar = false;

            // 2. Thiết lập độ trong suốt
            this.BackColor = Color.Magenta;
            this.TransparencyKey = Color.Magenta;

            // 3. Layered Window giúp mượt hơn và không chặn click chuột
            int exStyle = GetWindowLong(this.Handle, -20);
            SetWindowLong(this.Handle, -20, exStyle | 0x80000 | 0x20); // WS_EX_LAYERED | WS_EX_TRANSPARENT

            UpdateTaskbarPosition();
            UpdateSnowCache();

            // Khởi tạo tuyết
            for (int i = 0; i < SnowConfig.SoLuongTuyet; i++)
                fallingFlakes.Add(new Snowflake(rand.Next(this.Width), rand.Next(this.Height), rand));

            // 4. Timer tối ưu (khoảng 50-60 FPS)
            int fps = Math.Max(1, SnowConfig.MucFPS);
            timer.Interval = 1000 / fps;
            timer.Tick += delegate
            {
                UpdatePhysics();
                // Đẩy form xuống dưới cùng nếu muốn tuyết rơi sau icon
                SetWindowPos(this.Handle, HWND_BOTTOM, 0, 0, 0, 0, 0x0001 | 0x0002 | 0x0010);
                this.Invalidate();
            };
            timer.Start();
        }




        private void UpdateSnowCache()
        {
            // 1. Lấy danh sách tất cả các ký tự cần có trong cache
            List<string> allShapes = new List<string>(SnowConfig.GetActiveShapes());

            // Đảm bảo chuỗi bí mật luôn có trong danh sách cần kiểm tra cache
            if (!allShapes.Contains(SnowConfig.ChuoiBiMat))
            {
                allShapes.Add(SnowConfig.ChuoiBiMat);
            }

            // 2. Kiểm tra nhanh xem có cần cập nhật không
            bool hasMissingShape = false;
            foreach (string s in allShapes)
            {
                if (!snowCache.ContainsKey(s)) { hasMissingShape = true; break; }
            }

            // Nếu không thiếu hình nào và màu sắc vẫn cũ thì thoát sớm
            if (!hasMissingShape && lastMauTuyet == SnowConfig.MauTuyet) return;

            // 3. Nếu đổi màu, xóa toàn bộ cache cũ để vẽ lại theo màu mới
            if (lastMauTuyet != SnowConfig.MauTuyet)
            {
                foreach (var img in snowCache.Values) img.Dispose();
                snowCache.Clear();
                lastMauTuyet = SnowConfig.MauTuyet;
            }

            // 4. Duyệt qua danh sách và nạp những hình còn thiếu vào cache
            foreach (string s in allShapes)
            {
                if (!snowCache.ContainsKey(s))
                {
                    snowCache[s] = CreateBitmapForText(s);
                }
            }
        }

        // Hàm bổ trợ: Vẽ ký tự ra Bitmap dựa trên độ dài chuỗi
        private Bitmap CreateBitmapForText(string text)
        {
            text = text.Trim();
            int charCount = text.Length;

            // Tính toán chiều rộng động: 1 ký tự = 100px, từ ký tự thứ 2 cộng thêm 80px/ký tự
            int bmpWidth = (charCount <= 1) ? 100 : (100 + (charCount - 1) * 80);
            int bmpHeight = 100;

            Bitmap bmp = new Bitmap(bmpWidth, bmpHeight);
            try
            {
                using (Graphics g = Graphics.FromImage(bmp))
                {
                    g.SmoothingMode = SmoothingMode.AntiAlias;
                    g.TextRenderingHint = System.Drawing.Text.TextRenderingHint.AntiAliasGridFit;

                    // Sử dụng font hỗ trợ emoji tốt
                    using (Font f = new Font("Segoe UI Symbol", 60, FontStyle.Bold))
                    using (SolidBrush b = new SolidBrush(SnowConfig.MauTuyet))
                    {
                        SizeF textSize = g.MeasureString(text, f);
                        float posX = (bmpWidth - textSize.Width) / 2;
                        float posY = (bmpHeight - textSize.Height) / 2;

                        g.DrawString(text, f, b, posX, posY);
                    }
                }
            }
            catch
            {
                // Trả về bitmap trống nếu có lỗi vẽ để tránh crash
            }
            return bmp;
        }







        private void UpdatePhysics()
        {
            UpdateTaskbarPosition();
            UpdateSnowCache();

            // --- Logic gió ---
            cycleTimerCount--;
            if (cycleTimerCount <= 0)
            {
                isWindyPeriod = !isWindyPeriod;
                targetWindX = isWindyPeriod ? rand.Next(-SnowConfig.CuongDoGio, SnowConfig.CuongDoGio + 1) : 0;
                cycleTimerCount = (isWindyPeriod ? SnowConfig.ThoiGianThoiS : SnowConfig.ThoiGianNgatS) * 33;
            }
            currentWindX += (targetWindX - currentWindX) * 0.03f;

            Point mPos = Cursor.Position;
            bool isMouseDown = (GetAsyncKeyState(0x01) & 0x8000) != 0;

            // --- Xử lý Tuyết đang rơi ---
            for (int i = fallingFlakes.Count - 1; i >= 0; i--)
            {
                Snowflake f = fallingFlakes[i];
                if (isMouseDown && IsNear(f.X, f.Y, mPos)) { f.Reset(this.Width, rand); continue; }

                f.Y += f.Speed;
                f.X += (int)(currentWindX * 0.5f);
                f.Angle += f.RotationSpeed + (currentWindX * 0.4f);

                int hitY;
                if (TryGetCollisionY(f, out hitY))
                {
                    // TỐI ƯU: Đếm mật độ và tìm hạt dưới cùng TRỰC TIẾP, không tạo List mới
                    int countNearby = 0;
                    int bottomIndex = -1;
                    int maxY = -1;

                    // Chỉ duyệt stuckFlakes khi thực sự có va chạm (tiết kiệm cực nhiều CPU)
                    for (int j = 0; j < stuckFlakes.Count; j++)
                    {
                        if (Math.Abs(stuckFlakes[j].X - f.X) < 12)
                        {
                            countNearby++;
                            if (stuckFlakes[j].Y > maxY)
                            {
                                maxY = stuckFlakes[j].Y;
                                bottomIndex = j;
                            }
                        }
                    }

                    if (countNearby >= SnowConfig.GioiHamMatDo && bottomIndex != -1)
                    {
                        stuckFlakes.RemoveAt(bottomIndex);
                    }

                    f.Y = hitY;
                    f.IsStuck = true;
                    f.LifeTime = SnowConfig.ThoiGianTanS * 33;
                    // Công thức tính Opacity và Độ đóng băng của bạn giữ nguyên
                    f.Opacity = 255 - (int)(SnowConfig.DoDongBang * 1.05f);

                    stuckFlakes.Add(f);
                    fallingFlakes.RemoveAt(i);
                }
                else if (f.Y > this.Height) f.Reset(this.Width, rand);
            }

            // --- Xử lý Tuyết đã đọng ---
            for (int i = stuckFlakes.Count - 1; i >= 0; i--)
            {
                Snowflake s = stuckFlakes[i];
                if (isMouseDown && IsNear(s.X, s.Y, mPos)) { stuckFlakes.RemoveAt(i); continue; }

                s.LifeTime--;
                if (s.LifeTime <= 0)
                {
                    s.Opacity -= 5;
                    if (s.Opacity <= 0) { stuckFlakes.RemoveAt(i); continue; }
                }

                int nextY;
                // Tuyết lở: Nếu mất vật đỡ thì rơi tiếp
                if (!TryGetCollisionY(s, out nextY)) s.Y += 2;
                else if (s.Y < nextY - 1) s.Y += 1;
            }

            // --- Giới hạn tổng số hạt (Giữ nguyên 4000 của bạn) ---
            int gioiHanThucTe = Math.Min(SnowConfig.SoLuongTuyetToiDaTrenManHinh, 4000);
            if (stuckFlakes.Count > gioiHanThucTe)
            {
                stuckFlakes.RemoveAt(0);
            }

            while (fallingFlakes.Count < SnowConfig.SoLuongTuyet)
                fallingFlakes.Add(new Snowflake(rand.Next(this.Width), -30, rand));
        }



        protected override void OnPaint(PaintEventArgs e)
        {
            Graphics g = e.Graphics;
            g.Clear(this.TransparencyKey);

            // Cài đặt tốc độ vẽ tối đa
            g.SmoothingMode = SmoothingMode.None;
            g.InterpolationMode = InterpolationMode.Low;
            g.CompositingQuality = CompositingQuality.HighSpeed;

            // --- 1. VẼ TUYẾT RƠI (Có xoay) ---
            foreach (Snowflake f in fallingFlakes)
            {
                if (!snowCache.ContainsKey(f.Shape)) continue;

                Bitmap img = snowCache[f.Shape];
                float aspect = (float)img.Width / img.Height;
                float drawWidth = f.Size * aspect;
                float drawHeight = f.Size;

                // Lưu trạng thái để xoay
                GraphicsState state = g.Save();
                g.TranslateTransform(f.X, f.Y);
                g.RotateTransform(f.Angle);

                // Vẽ từ tâm
                g.DrawImage(img, -drawWidth / 2, -drawHeight / 2, drawWidth, drawHeight);
                g.Restore(state);
            }

            // --- 2. VẼ TUYẾT ĐỌNG (Tối ưu: Không xoay, dùng chung ColorMatrix) ---
            if (stuckFlakes.Count > 0)
            {
                using (var attr = new System.Drawing.Imaging.ImageAttributes())
                {
                    // Khởi tạo ma trận màu một lần duy nhất dùng chung cho cả vòng lặp
                    var cm = new System.Drawing.Imaging.ColorMatrix();

                    foreach (Snowflake s in stuckFlakes)
                    {
                        if (!snowCache.ContainsKey(s.Shape)) continue;

                        Bitmap img = snowCache[s.Shape];
                        float aspect = (float)img.Width / img.Height;
                        int drawWidth = (int)(s.Size * aspect);
                        int drawHeight = s.Size;

                        if (s.Opacity < 255)
                        {
                            // Thay đổi giá trị Alpha trong ma trận hiện tại (không tạo mới)
                            cm.Matrix33 = s.Opacity / 255f;
                            attr.SetColorMatrix(cm);

                            g.DrawImage(img, new Rectangle(s.X, s.Y, drawWidth, drawHeight),
                                        0, 0, img.Width, img.Height, GraphicsUnit.Pixel, attr);
                        }
                        else
                        {
                            // Vẽ cực nhanh: Không xoay, không ma trận màu
                            g.DrawImage(img, s.X, s.Y, drawWidth, drawHeight);
                        }
                    }
                }
            }
        }





        private void UpdateTaskbarPosition()
        {
            IntPtr taskbarHwnd = FindWindow("Shell_TrayWnd", null);
            RECT r; if (GetWindowRect(taskbarHwnd, out r)) taskbarTop = r.Top; else taskbarTop = this.Height - 40;
        }

        private bool IsNear(int x, int y, Point m)
        {
            return Math.Sqrt(Math.Pow(x - m.X, 2) + Math.Pow(y - m.Y, 2)) < SnowConfig.BanKinhQuet;
        }


        private bool IsFullScreen(IntPtr hWnd, RECT windowRect)
        {
            Rectangle screenBounds = Screen.PrimaryScreen.Bounds;
            int windowHeight = windowRect.Bottom - windowRect.Top;
            int windowWidth = windowRect.Right - windowRect.Left;
            bool isTooTall = windowHeight > (screenBounds.Height * (float)SnowConfig.TyLeChieuCaoManHinh / 100f);
            bool isWideEnough = windowWidth > (screenBounds.Width * (float)SnowConfig.TyLeChieuRongManHinh / 100f);

            return isTooTall && isWideEnough;
        }



        private bool TryGetCollisionY(Snowflake f, out int finalY)
        {
            finalY = 0;

            // --- CẤU HÌNH CÁC TỶ LỆ NÚN (Hằng số) ---
            float TY_LE_NOI_MAC_DINH = SnowConfig.TyLeNoiMacDinh;     // Nún 50% khi rơi bình thường
            float TY_LE_NOI_KHI_SUT_LO = SnowConfig.TyLeNoiSutLo;  // Nún 75% (nún thêm 50% của phần nổi) khi bị sụt lở
            float TY_LE_NOI_VAT_THE_CUNG = SnowConfig.TyLeNoiVatCung; // Không nún khi chạm Taskbar/Cửa sổ



            // Xác định tỷ lệ nổi hiện tại dựa trên trạng thái hạt
            float tyLeNoiHienTai = f.IsFallingAgain ? TY_LE_NOI_KHI_SUT_LO : TY_LE_NOI_MAC_DINH;

            // Tính toán các điểm quét va chạm
            int hitY_TuyetCu = f.Y + (int)(f.Size * tyLeNoiHienTai);
            int hitY_VatTheCung = f.Y + (int)(f.Size * TY_LE_NOI_VAT_THE_CUNG);

            // --- BƯỚC 1: VA CHẠM VỚI TUYẾT CŨ (Xây núi - Ưu tiên hàng đầu) ---

            // Duyệt ngược danh sách để luôn va chạm với hạt trên đỉnh trước
            for (int i = stuckFlakes.Count - 1; i >= 0; i--)
            {
                Snowflake other = stuckFlakes[i];

                // Bỏ qua chính nó nếu đang xét hạt đọng
                if (f == other) continue;

                // Kiểm tra khoảng cách ngang (X) để xác định cột tuyết
                if (Math.Abs(f.X - other.X) < 15)
                {
                    // Nếu điểm quét va chạm vượt quá tọa độ Y của hạt bên dưới
                    if (hitY_TuyetCu >= other.Y && f.Y < other.Y)
                    {
                        // Tính tọa độ đậu dựa trên tỷ lệ nún tương ứng
                        finalY = other.Y - (int)(f.Size * tyLeNoiHienTai);

                        // Reset trạng thái sụt lở vì đã tìm được chỗ đậu mới
                        f.IsFallingAgain = false;
                        return true;
                    }
                }
            }


            // --- BƯỚC 2: VA CHẠM VỚI CỬA SỔ (Ứng dụng bên thứ 3) ---
            IntPtr hWnd = WindowFromPoint(new Point(f.X, hitY_VatTheCung));
            if (hWnd != IntPtr.Zero && hWnd != this.Handle)
            {
                IntPtr rootHWnd = GetAncestor(hWnd, GA_ROOT);
                RECT r;
                if (GetWindowRect(rootHWnd, out r))
                {
                    // Chỉ đậu nếu cửa sổ không ở chế độ toàn màn hình
                    if (!IsFullScreen(rootHWnd, r))
                    {
                        // Kiểm tra nếu chạm vào dải 15px trên cùng của cửa sổ
                        if (r.Top > 10 && hitY_VatTheCung >= r.Top && hitY_VatTheCung <= r.Top + 15 &&
                            f.X >= r.Left && f.X <= r.Right)
                        {
                            finalY = r.Top - f.Size; // Đậu khít trên mép
                            f.IsFallingAgain = false;
                            return true;
                        }
                    }
                }
            }

            // --- BƯỚC 3: VA CHẠM VỚI THANH TASKBAR (Sàn dưới cùng) ---
            if (hitY_VatTheCung >= taskbarTop)
            {
                finalY = taskbarTop - f.Size; // Đậu khít trên Taskbar
                f.IsFallingAgain = false;
                return true;
            }

            // Không có va chạm nào
            return false;
        }






        public void ClearStuckSnow() { stuckFlakes.Clear(); }
    }





    // 4. Form Cài đặt
    public class SettingsForm : Form
    {
        public bool AllowClose = false;
        private ToolTip tip = new ToolTip();

        public SettingsForm()
        {
            // 1. Ép chế độ hiển thị chuẩn để không bị vỡ chữ trên Win 7
            this.AutoScaleMode = AutoScaleMode.None;
            this.Font = new Font("Segoe UI", 9F);

            // 2. Thiết lập Form nhỏ gọn, chuyên nghiệp
            this.Text = "HaHa Cài đặt/ Setting Snow Fall Desktop";
            this.Size = new Size(650, 580); // Kích thước vừa vặn cho 2 cột
            this.BackColor = Color.FromArgb(250, 250, 250);
            this.FormBorderStyle = FormBorderStyle.FixedSingle;
            this.MaximizeBox = false;

            // QUAN TRỌNG: Hiển thị như một phần mềm bình thường
            this.StartPosition = FormStartPosition.CenterScreen;
            this.ShowInTaskbar = true; // Hiện ở Taskbar để Windows ưu tiên lớp hiển thị
            this.TopMost = false;      // Không chiếm quyền ưu tiên tuyệt đối nhưng sẽ nổi lên khi Activate

            // 3. Lưới 2 cột chính
            TableLayoutPanel grid = new TableLayoutPanel();
            grid.Dock = DockStyle.Fill;
            grid.ColumnCount = 2;
            grid.Padding = new Padding(15, 15, 15, 80); // Chừa lề dưới cho nút bấm
            grid.AutoScroll = true;

            grid.ColumnStyles.Add(new ColumnStyle(SizeType.Percent, 50f));
            grid.ColumnStyles.Add(new ColumnStyle(SizeType.Percent, 50f));

            // 4. Đổ đầy các chức năng cũ (Sử dụng hàm AddItem mới)





            // --- CỘT 1 & 2: CÁC THÔNG SỐ CƠ BẢN ---
            AddItem(grid, "Số tuyết rơi / Snow count", 5, 200, SnowConfig.SoLuongTuyet, v => SnowConfig.SoLuongTuyet = v);
            AddItem(grid, "Tuyết tối đa / Max flakes", 500, 4000, SnowConfig.SoLuongTuyetToiDaTrenManHinh, v => SnowConfig.SoLuongTuyetToiDaTrenManHinh = v);
            AddItem(grid, "Tốc độ rơi nhỏ nhất/ Min Speed", 1, 10, SnowConfig.TocDoMin, v => SnowConfig.TocDoMin = v);
            AddItem(grid, "Tốc độ rơi lớn nhất / Max Speed", 1, 20, SnowConfig.TocDoMax, v => SnowConfig.TocDoMax = v);
            AddItem(grid, "Giới hạn mật độ / Density limit", 1, 200, SnowConfig.GioiHamMatDo, v => SnowConfig.GioiHamMatDo = v);
            AddItem(grid, "Kích thước hạt / Snowflake size", 5, 50, SnowConfig.KichThuoc, v => SnowConfig.KichThuoc = v);

            // --- CỘT 1 & 2: VẬT LÝ TUYẾT ---
            AddItem(grid, "Độ xốp / Fluffiness (%)", 0, 100, (int)(SnowConfig.TyLeNoiMacDinh * 100), v => SnowConfig.TyLeNoiMacDinh = v / 100f);
            AddItem(grid, "Độ nén / Compression (%)", 0, 100, (int)(SnowConfig.TyLeNoiSutLo * 100), v => SnowConfig.TyLeNoiSutLo = v / 100f);
            AddItem(grid, "Độ nổi vật cứng / Hardness (%)", 30, 100, (int)(SnowConfig.TyLeNoiVatCung * 100), v => SnowConfig.TyLeNoiVatCung = v / 100f);
            AddItem(grid, "Độ đóng băng / Icing (%)", 0, 100, SnowConfig.DoDongBang, v => SnowConfig.DoDongBang = v);

            // --- CỘT 1 & 2: GIÓ & HIỆU NĂNG ---
            AddItem(grid, "Cường độ gió / Wind strength", 0, 50, SnowConfig.CuongDoGio, v => SnowConfig.CuongDoGio = v);
            AddItem(grid, "Gió thổi / Wind duration (s)", 1, 30, SnowConfig.ThoiGianThoiS, v => SnowConfig.ThoiGianThoiS = v);
            AddItem(grid, "Gió ngắt / Wind interval (s)", 1, 30, SnowConfig.ThoiGianNgatS, v => SnowConfig.ThoiGianNgatS = v);
            AddItem(grid, "Thời gian tan / Melt time (s)", 1, 120, SnowConfig.ThoiGianTanS, v => SnowConfig.ThoiGianTanS = v);
            AddItem(grid, "Chỉnh FPS / FPS limit", 5, 60, SnowConfig.MucFPS, v => SnowConfig.MucFPS = v);
            AddItem(grid, "Bán kính dọn dẹp/ Clean radius", 10, 200, SnowConfig.BanKinhQuet, v => SnowConfig.BanKinhQuet = v);

            // --- CỘT 1 & 2: VÙNG ĐÓN TUYẾT / SNOW CATCHING AREA ---
            AddItem(grid, "Chiều rộng / Width area (%)", 10, 100, SnowConfig.TyLeChieuRongManHinh, v => SnowConfig.TyLeChieuRongManHinh = v,
                    "Tỷ lệ chiều rộng cửa sổ ứng dụng để tuyết có thể đậu lên \nWindow width ratio for snow to land");

            AddItem(grid, "Chiều cao / Height area (%)", 10, 100, SnowConfig.TyLeChieuCaoManHinh, v => SnowConfig.TyLeChieuCaoManHinh = v,
                    "Tỷ lệ chiều cao cửa sổ ứng dụng để tuyết có thể đậu lên \nWindow height ratio for snow to land");

            // Ô nhập ký tự thêm (Chiếm 2 cột)
            Panel pnlText = new Panel() { Width = 580, Height = 55, Margin = new Padding(0, 10, 0, 20) };
            grid.SetColumnSpan(pnlText, 2);
            Label lblT = new Label() { Text = "Thêm ký tự và Emoji (cách nhau bởi dấu phẩy)/ Add characters and Emojis (separated by commas):", AutoSize = true, ForeColor = Color.DimGray };
            TextBox txt = new TextBox() { Text = SnowConfig.KyTuThem, Width = 580, Top = 20, BorderStyle = BorderStyle.FixedSingle };
            txt.TextChanged += (s, e) => SnowConfig.KyTuThem = txt.Text;
            pnlText.Controls.Add(lblT); pnlText.Controls.Add(txt);
            grid.Controls.Add(pnlText);

            // 5. Panel nút bấm cố định phía dưới
            Panel pnlBottom = new Panel() { Dock = DockStyle.Bottom, Height = 70, BackColor = Color.White };
            pnlBottom.Paint += (s, e) => e.Graphics.DrawLine(Pens.Gainsboro, 0, 0, pnlBottom.Width, 0);



            // MÀU TUYẾT
            Button btnColor = CreateBtn("MÀU TUYẾT/ CHANGE COLOUR", 30, Color.DodgerBlue, () =>
            {
                using (ColorDialog cd = new ColorDialog() { Color = SnowConfig.MauTuyet })
                    if (cd.ShowDialog() == DialogResult.OK) SnowConfig.MauTuyet = cd.Color;
            });


            Button btnClose = CreateBtn("ĐÓNG/ CLOSE", 330, Color.DimGray, () => this.Hide());

            pnlBottom.Controls.Add(btnColor);
            pnlBottom.Controls.Add(btnClose);

            this.Controls.Add(grid);
            this.Controls.Add(pnlBottom);

            this.FormClosing += (s, e) => { if (e.CloseReason == CloseReason.UserClosing) { e.Cancel = true; this.Hide(); } };
        }




        private void AddItem(TableLayoutPanel parent, string name, int min, int max, int cur, Action<int> onScroll, string description = "")
        {
            Panel p = new Panel() { Width = 310, Height = 65, Margin = new Padding(5) };

            Label l = new Label()
            {
                Text = name + ": " + cur,
                AutoSize = true,
                Font = new Font("Segoe UI", 8.5F, FontStyle.Bold),
                ForeColor = Color.FromArgb(60, 60, 60)
            };

            TrackBar t = new TrackBar()
            {
                Minimum = min,
                Maximum = max,
                Value = Math.Max(min, Math.Min(max, cur)),
                Width = 290,
                Top = 25,
                TickStyle = TickStyle.None,
                Height = 30
            };

            // CHỈ GÁN NẾU CÓ NHẬP CHÚ THÍCH
            if (!string.IsNullOrEmpty(description))
            {
                tip.SetToolTip(l, description);
                tip.SetToolTip(t, description);
            }

            t.Scroll += (s, e) =>
            {
                onScroll(t.Value);
                l.Text = name + ": " + t.Value;
            };

            p.Controls.Add(l); p.Controls.Add(t);
            parent.Controls.Add(p);
        }




        private Button CreateBtn(string txt, int x, Color c, Action onClick)
        {
            Button b = new Button()
            {
                Text = txt,
                Location = new Point(x, 15),
                Size = new Size(270, 40),
                FlatStyle = FlatStyle.Flat,
                BackColor = c,
                ForeColor = Color.White,
                Font = new Font("Segoe UI", 9F, FontStyle.Bold),
                Cursor = Cursors.Hand
            };
            b.FlatAppearance.BorderSize = 0;
            b.Click += (s, e) => onClick();
            return b;
        }
    }


